import ReactSlider from "react-slider";
import './Slider.css'

function Settings() {
    return (
        <div style={{textAlign: "left"}}>
<label>work minutes:</label>
<ReactSlider 
className={"slider"}
thumbClassName={'thumb'}
value={45}
min={1}
max={120}
/>
<label>break minutes:</label>
<ReactSlider 
className={"slider green"}
thumbClassName={'thumb'}
value={45}
min={1}
max={120}
/>
        </div>
    );
}

export default Settings;
